import React, { useContext, useEffect, useState } from 'react';
import axios from "axios";
import { store } from './store.js';
import logo from './logo.svg';
import './App.css';
import Login from './GoogleLogin';
import { Input, Button, Link} from './styled';


function App() {
  const [users, setUsers] = useState([]);
  const[user, setUser] = useState(null);
  const[newUser, setNewUser]  = useState({ name:'', job:''})
  const globalState = useContext(store);

  // De forma similar a componentDidMount y componentDidUpdate
  useEffect(() => {
    // Actualiza el tÃ­tulo del documento usando la API del navegador
    axios.get("https://reqres.in/api/users?page=1&per_page=5").then(response => {
      console.log(response.data, 'Data', response.data.data)
      setUsers(response.data.data)
    })
  }, []);

  
  
  const ExampleComponent = () => {
    const globalState = useContext(store);
    console.log(globalState.state.userBw); // this will return { color: red }
  };

  const ExampleDispatch = () => {
    
    const { dispatch } = globalState;
  
    dispatch({ type: 'action description' })
  };

  const getUsers = (id) => {
    axios.get("https://reqres.in/api/users/" + id).then(response => {
      console.log(response.data, 'Data', response.data.data)
      setUser(response.data)
    })
  }

  const createUsers = () => {
    axios.post("https://reqres.in/api/users/", newUser).then(response => {
      console.log(response.data, 'Data', response.data.data)
    })
  }

  const updateUsers = (id) => {
    axios.patch("https://reqres.in/api/users/"+ id, newUser).then(response => {
      console.log(response.data, 'Data', response.data.data)
    })
  }
  
  

  return (
    <div className="App">
      {ExampleComponent()}
      <header className="App-header">
        <img src={globalState.state.user.Bw.profiileObj.imageUrl} className="App-logo" alt="logo" />
        <div>
            {globalState.state.user.Bw.profiileObj.name}
        </div>
        <div>
            {globalState.state.user.Bw.profiileObj.email}
        </div>
        <p onClick={() => ExampleDispatch()}>
          Usuarios
        </p>
        
        <Input onChange={(event) => setNewUser({ ...newUser, name: event.target.value})}/>
        <Input onChange={(event) => setNewUser({ ...newUser, job: event.target.value})}/>
        <Button onClick={() => createUsers()}>Enviar</Button>
        <div style={{ display: 'flex'}}>
          <div>
            {
              users.map((user) => 
                <Link onClick={() => getUsers(user.id)}>{user.email}</Link>
              )
            } 
          </div>
          <div style={{ marginLeft: '90px'}}>
          {
            user &&
            /*My Project 12084
            67870201376-iiqfivfjr62r3uba2r8b45ievvr87bgm.apps.googleusercontent.com
            HyDidi-PGD1Hj8-NzHZrrpGJ*/
              <> 
              <div>
                <div>
                  {user.data.email}
                </div>
                <div>
                  {user.data.first_name}
                </div>
                <div>
                  {user.data.last_name}
                </div>
                <img src={user.data.avatar}  alt="logo" />
              </div>
              <Button onClick={() => updateUsers(user.data.id)}>Update</Button>
              </>
            }
            
          </div>
          
        </div>
       
        
      </header>
    </div>
  );
}

export default App;
