import React, { useContext, useEffect, useState } from 'react';
import { useGoogleLogin } from 'react-google-login';
import { store } from './store.js';
import { refreshToken } from './refreshToken';
import cloud from './cloud-district.jpg'
import { Button, WrapperLogin, LoginTitle } from './styled';

function Login() { 
    const globalState = useContext(store);
    const onSuccess = (response) => {
        console.log('Login', response);
        refreshToken(response);
        const { dispatch } = globalState;
        dispatch({ type: 'action description', payload: response })
    }

    const onFailure = (response) => {
        console.log('Failure', response);
    }

    const {signIn, loaded } = useGoogleLogin({
        clientId:"67870201376-iiqfivfjr62r3uba2r8b45ievvr87bgm.apps.googleusercontent.com",
        onSuccess,
        onFailure,
        cookiePolicy:'single_host_origin',
        isSignedIn:true,
    })

    return(
        <WrapperLogin>
           
            <img src={cloud} alt="logo" />
            <div>
                <LoginTitle>Login</LoginTitle>
                <Button onClick={signIn}>
                    <span>Sign in with Google</span>
                </Button>
            </div>
            
            
        </WrapperLogin>
    );
}
export default Login;
