import React, { useContext, useEffect, useState } from 'react';
import axios from "axios";
import { store } from './store.js';
import logo from './logo.svg';
import './App.css';
import { Input, Button} from './styled';


function App() {
  const [users, setUsers] = useState([]);
  const globalState = useContext(store);

  // De forma similar a componentDidMount y componentDidUpdate
  useEffect(() => {
    // Actualiza el título del documento usando la API del navegador
    axios.get("https://reqres.in/api/users?page=1&per_page=5").then(response => {
      console.log(response.data, 'Data', response.data.data)
      setUsers(response.data.data)
    })
  }, []);



  const ExampleComponent = () => {
    const globalState = useContext(store);
    console.log(globalState); // this will return { color: red }
  };

  const ExampleDispatch = () => {

    const { dispatch } = globalState;

    dispatch({ type: 'action description' })
  };

  const getUser = (id) => {
    axios.get("https://reqres.in/api/users/" + id).then(response => {
      console.log(response.data, 'Data', response.data.data)
      setUsers(response.data.data)
    })
  }



  return (
    <div className="App">
      {ExampleComponent()}
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p onClick={() => ExampleDispatch()}>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <Input />
        <Button>Enviar</Button>
        {
          users.map((user) => 
            <div onClick={getUsers}>{user.email}</div>
          )
        } 
      </header>
    </div>
  );
}

export default App;
