import styled from 'styled-components'

export const Button = styled.button`
  display: inline-block;
  color: palevioletred;
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
  display: block;
`;

export const Input = styled.input`
  padding: 0.5em;
  margin: 0.5em;
  color: ${props => props.inputColor || "palevioletred"};
  background: papayawhip;
  border: none;
  border-radius: 3px;
`;

export const Link = styled.div`
  cursor: pointer;
  &:hover {
    color: blue; // <Thing> when hovered
  }
  
`

export const WrapperLogin = styled.div`
  display: flex;
  flex-direction: column;
  color: blue;
  justify-content: center;
  align-items: center;
  height: 50vh;
`

export const AppHeader = styled.div`
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-size: calc(10px + 2vmin);
    color: #282c34;
`

export const InfoGoogle = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: calc(10px + 2vmin);
    border: 2px solid #BA5B9A;
    border-radius: 10px;
    padding: 40px 80px;
    -webkit-box-shadow: 6px 10px 5px 0px rgba(204,151,151,1);
-moz-box-shadow: 6px 10px 5px 0px rgba(204,151,151,1);
box-shadow: 6px 10px 5px 0px rgba(204,151,151,1);
`

export const ListUsers = styled.div`
